/**
 * Bicep Key Vault Template
 * 
 * Azure Key Vault deployment template
 */

const template = `// Azure Key Vault
// Generated by AgenticCoder

targetScope = 'resourceGroup'

// ============================================================================
// Parameters
// ============================================================================

@description('The name of the Key Vault')
@minLength(3)
@maxLength(24)
param keyVaultName string

@description('Location for all resources')
param location string = resourceGroup().location

@description('The SKU of the Key Vault')
@allowed([
  'standard'
  'premium'
])
param sku string = 'standard'

@description('Enable soft delete')
param enableSoftDelete bool = true

@description('Soft delete retention in days')
@minValue(7)
@maxValue(90)
param softDeleteRetentionInDays int = 90

@description('Enable purge protection')
param enablePurgeProtection bool = true

@description('Enable RBAC authorization')
param enableRbacAuthorization bool = true

@description('Enable public network access')
param publicNetworkAccess string = 'Enabled'

@description('Object IDs to grant access policies (ignored if RBAC is enabled)')
param accessPoliciesObjectIds array = []

@description('Tags for all resources')
param tags object = {}

// ============================================================================
// Variables
// ============================================================================

var keyVaultNameCleaned = 'kv-\${keyVaultName}'
var commonTags = union(tags, {
  'managed-by': 'bicep'
})

// ============================================================================
// Resources
// ============================================================================

// Key Vault
resource keyVault 'Microsoft.KeyVault/vaults@2023-07-01' = {
  name: keyVaultNameCleaned
  location: location
  tags: commonTags
  properties: {
    tenantId: subscription().tenantId
    sku: {
      family: 'A'
      name: sku
    }
    enableSoftDelete: enableSoftDelete
    softDeleteRetentionInDays: softDeleteRetentionInDays
    enablePurgeProtection: enablePurgeProtection ? true : null
    enableRbacAuthorization: enableRbacAuthorization
    publicNetworkAccess: publicNetworkAccess
    networkAcls: {
      defaultAction: 'Allow'
      bypass: 'AzureServices'
    }
    accessPolicies: enableRbacAuthorization ? [] : [for objectId in accessPoliciesObjectIds: {
      tenantId: subscription().tenantId
      objectId: objectId
      permissions: {
        keys: [
          'get'
          'list'
          'create'
          'delete'
          'update'
        ]
        secrets: [
          'get'
          'list'
          'set'
          'delete'
        ]
        certificates: [
          'get'
          'list'
          'create'
          'delete'
          'update'
        ]
      }
    }]
  }
}

// Diagnostic Settings (optional)
{{#if enableDiagnostics}}
resource diagnosticSettings 'Microsoft.Insights/diagnosticSettings@2021-05-01-preview' = {
  name: '\${keyVault.name}-diagnostics'
  scope: keyVault
  properties: {
    workspaceId: logAnalyticsWorkspaceId
    logs: [
      {
        categoryGroup: 'allLogs'
        enabled: true
        retentionPolicy: {
          enabled: true
          days: 30
        }
      }
    ]
    metrics: [
      {
        category: 'AllMetrics'
        enabled: true
        retentionPolicy: {
          enabled: true
          days: 30
        }
      }
    ]
  }
}
{{/if}}

// ============================================================================
// Outputs
// ============================================================================

@description('The name of the Key Vault')
output keyVaultName string = keyVault.name

@description('The resource ID of the Key Vault')
output keyVaultId string = keyVault.id

@description('The URI of the Key Vault')
output keyVaultUri string = keyVault.properties.vaultUri
`;

/**
 * Prepare variables for Key Vault template
 */
function prepareVariables(config) {
  return {
    enableDiagnostics: config.enableDiagnostics || false
  };
}

module.exports = {
  template,
  prepareVariables
};
